import dotenv from 'dotenv';

dotenv.config();

const FIXER_API_KEY = process.env.FIXER_API_KEY || '';
const FIXER_URL = process.env.FIXER_URL || 'http://data.fixer.io/api/latest';

const SERVER_PORT = process.env.SERVER_PORT ? Number(process.env.SERVER_PORT) : 9000;
const API_KEY = process.env.API_KEY || '';
const DEFAULT_FEE = process.env.DEFAULT_FEE ? Number(process.env.DEFAULT_FEE) : 0.01;

const config = {
  server: {
    port: SERVER_PORT,
    apiKey: API_KEY,
  },
  app: {
    defaultFee: DEFAULT_FEE,
  },
  api: {
    fixer: {
      key: FIXER_API_KEY,
      url: FIXER_URL,
    },
  },
};

export default config;

import App from './server/app';
import RatesController from './controllers/ratesController';
import HealthController from './controllers/healthController';
import config from './config';

const app = new App({
  routes: [
    ['GET', '/rates', new RatesController()],
    ['GET', '/healthcheck', new HealthController()],
  ],
  serverPort: config.server.port,
});

export default app;

import Hapi, { Server } from '@hapi/hapi';
import * as HapiSwagger from 'hapi-swagger';
import Inert from '@hapi/inert';
import Vision from '@hapi/vision';
import { AppRoute, AppParams } from '../typings';
import authScheme from './authScheme';
import swaggerOptions from './swaggerOptions';

class App {
  public server: Server;

  public port: number;

  public routes: AppRoute[];

  constructor(params: AppParams) {
    const { routes, serverPort } = params;
    this.port = serverPort;
    this.routes = routes;
    this.server = this.init();
  }

  public init() {
    this.server = Hapi.server({
      port: this.port,
      host: '0.0.0.0',
    });
    this.server.auth.scheme('basic', authScheme);
    this.server.auth.strategy('simple', 'basic');
    this.registerRoutes(this.routes);
    return this.server;
  }

  private registerRoutes = (routes: AppRoute[]) => {
    routes.forEach((route: AppRoute) => {
      const [method, path, controller] = route;
      this.server?.route({
        method,
        path,
        handler: controller.handler,
        options: {
          auth: 'simple',
          ...controller.options,
          validate: controller.validations,
          plugins: controller.plugins,
        },
      });
    });
  };

  public listen = async () => {
    try {
      const plugins: Array<Hapi.ServerRegisterPluginObject<any>> = [
        {
          plugin: Inert,
        },
        {
          plugin: Vision,
        },
        {
          plugin: HapiSwagger,
          options: swaggerOptions,
        },
      ];
      await this.server.register(plugins);
      await this.server.start();
      console.log(`Server running on ${this.server?.info?.uri}`);
    } catch (error: any) {
      console.log(`Error starting server: ${error.message}`);
    }
  };

  public stop = async () => {
    try {
      await this.server.stop();
      console.log('Server stopped');
    } catch (error: any) {
      console.log(`Error stopping server: ${error.message}`);
    }
  };
}

export default App;

import Hapi from '@hapi/hapi';
import Boom from 'boom';
import config from '../config';

const authScheme = () => ({
  authenticate: async (request: Hapi.Request, h: Hapi.ResponseToolkit) => {
    const { authorization } = request.headers;
    if (!authorization) {
      throw Boom.unauthorized('Missing authorization header', 'custom');
    }
    const [scheme, key] = authorization.split(' ');
    if (scheme !== 'Bearer') {
      throw Boom.unauthorized('Invalid authorization scheme', 'custom');
    }
    if (key !== config.server.apiKey) {
      throw Boom.unauthorized('Invalid API KEY', 'custom');
    }
    return h.authenticated({ credentials: { key } });
  },
});

export default authScheme;

import * as HapiSwagger from 'hapi-swagger';

const swaggerOptions: HapiSwagger.RegisterOptions = {
  info: {
    title: 'Test API Documentation',
  },
};

export default swaggerOptions;

import { Request, ResponseToolkit } from '@hapi/hapi';
import Joi from '@hapi/joi';
import { AppController } from '../typings';
import getRates from '../services/ratesService';

class RatesController implements AppController {
  public handler = async (request: Request, response: ResponseToolkit) => {
    // Sample call: http://localhost:3000/rates?fee=0.1&pairs=EURUSD,USDGBP
    const { fee, pairs } = request.query;
    console.log('Request: ', request.query);
    const parsedPairs = pairs.split(',');
    const rates = await getRates(parsedPairs, fee);
    return response.response(rates);
  };

  public validations = {
    query: Joi.object({
      fee: Joi.number().min(0).max(1).required(),
      pairs: Joi.string().required(),
    }),
  };

  public plugins = {
    'hapi-swagger': {
      responses: {
        200: {
          description: 'Success',
          schema: Joi.array().items(
            Joi.object({
              fee: Joi.number(),
              feeAmmount: Joi.number(),
              originalRate: Joi.number(),
              pair: Joi.object({
                from: Joi.string(),
                to: Joi.string(),
              }),
              rate: Joi.number(),
            }),
          ),
        },
      },
    },
  };
}

export default RatesController;

import NodeCache from 'node-cache';
import { Pair, PairInfo } from '../typings';
import { getFixerRates } from '../api/fixer';
import config from '../config';

const cache = new NodeCache();

/*
* Parse pairs from string to object
* @param {string[]} pairs
* @returns {Pair[]}
* @example
* parsePairs(['EURUSD', 'EURARS'])
* // => [{ from: 'EUR', to: 'USD' }, { from: 'EUR', to: 'ARS' }]
*/
const parsePairs = (pairs: string[]) => {
  const pairsParsed = pairs.map((pair) => {
    // Split pairs names. Example 'EURUSD' to ['EUR', 'USD']
    const [from, to] = pair.match(/.{1,3}/g) || [];
    return { from, to };
  }) as Pair[];
  return pairsParsed;
};

// Applies fee, feeAmmount and rate
const applyFee = (pairInfo: PairInfo, _fee: number) => {
  const fee = _fee || config.app.defaultFee;
  const { originalRate } = pairInfo;
  if (!originalRate) return pairInfo;
  const feeAmmount = Number((originalRate * fee).toFixed(4));
  const rate = Number((originalRate + feeAmmount).toFixed(4));
  return {
    ...pairInfo, fee, feeAmmount, rate,
  };
};

// Save pairs info in cache
const savePairsInfo = (pairsInfo: PairInfo[]) => {
  console.log('Saving pairs info in cache');
  const today = (new Date()).setHours(0, 0, 0, 0).toLocaleString();
  const currentPairs = cache.get('pairsInfo') as PairInfo[];
  const pairsFromToday = currentPairs?.filter((pairInfo) => pairInfo.date === today) || [];
  const pairsToStore = pairsInfo.map((pairInfo) => ({ ...pairInfo, date: today }));
  cache.set('pairsInfo', [...pairsFromToday, ...pairsToStore]);
};

// Get rates from cache
const getRatesFromCache = (pairs: Pair[], fee: number) => {
  const today = (new Date()).setHours(0, 0, 0, 0).toLocaleString();
  const currentPairs = cache.get('pairsInfo') as PairInfo[];
  const pairsFromToday = currentPairs?.filter((pairInfo) => pairInfo.date === today) || [];
  const pairsInCache: PairInfo[] = [];
  const pairsNotInCache: Pair[] = [];
  pairs.forEach((pair) => {
    const pairInfo = pairsFromToday.find((_pairInfo) => {
      const { from, to } = pair;
      const { pair: { from: _from, to: _to } } = _pairInfo;
      return (from === _from && to === _to);
    });
    if (pairInfo) {
      pairsInCache.push(pairInfo);
    } else {
      pairsNotInCache.push(pair);
    }
  });
  console.log('Pairs in cache:', pairsInCache.length);
  console.log('Pairs not in cache:', pairsNotInCache.length);
  // apply fee to pairs in cache
  const pairsInCacheWithFee = pairsInCache.map((pairInfo) => applyFee(pairInfo, fee));
  console.log('Pairs in cache with fee:', pairsInCacheWithFee);
  return { pairsInCacheWithFee, pairsNotInCache };
};

// Main function to get rates
const getRates = async (pairs: string[], fee: number) => {
  const pairsParsed = parsePairs(pairs);
  const { pairsInCacheWithFee, pairsNotInCache } = getRatesFromCache(pairsParsed, fee);
  const pairsInfo = await getFixerRates(pairsNotInCache);
  savePairsInfo(pairsInfo);
  const pairsWithFee = pairsInfo.map((pair: PairInfo) => applyFee(pair, fee));
  console.log('pairsWithFee', pairsWithFee);
  return [...pairsInCacheWithFee, ...pairsWithFee];
};

export default getRates;

import axios, { AxiosRequestConfig } from 'axios';
import {
  CurrencySymbol, FixerResponse, Pair, PairInfo,
} from '../typings';
import config from '../config';

const { url, key } = config.api.fixer;

// Group base symbols and set of symbols to get rates
// To avoid multiple requests to the API
// Example input: [{ from: 'EUR', to: 'USD' }, { from: 'EUR', to: 'ARS' }]
// Example result: [{ base: 'EUR', pairs: ['USD', 'ARS'] }]
const groupFixerPairs = (pairs: Pair[]) => {
  const baseCurrencies = pairs.map((pair) => pair.from);
  const uniqueBaseCurrencies = [...new Set(baseCurrencies)];
  const fixerPairs = uniqueBaseCurrencies.map((base) => {
    const symbols = pairs
      .filter((pair) => pair.from === base)
      .map((pair) => pair.to);
    return { base, symbols };
  });
  console.log('Grouped pairs: ', fixerPairs);
  return fixerPairs;
};

// Get rates from Fixer API
// Example: [{ base: 'EUR', pairs: ['USD', 'ARS'] }]
// Example of fixer result:
// { success: true, timestamp: 1620000000, base: 'EUR',
//   rates: { USD: 1.123, ARS: 123.45 } }
export const getFixerRate = async (base: CurrencySymbol, symbols: CurrencySymbol[]) => {
  const fixerUrl = `${url}?access_key=${key}&base=${base}&symbols=${symbols.join(',')}`;
  console.log('fetch: ', fixerUrl);
  const requesConfig: AxiosRequestConfig = {
    headers: {
      'Content-Type': 'application/json',
      apikey: key,
    },
  };
  const fixerResponse = await axios.get(fixerUrl, requesConfig) as FixerResponse;
  // If the pair is not allowed, reconstruct the response with base and symbols
  if (fixerResponse.data.error?.code === 105) {
    return {
      ...fixerResponse,
      data: {
        ...fixerResponse.data,
        rates: Object.fromEntries(symbols.map((symbol) => [symbol, undefined])),
        base,
      },
    } as unknown as FixerResponse;
  }
  return fixerResponse;
};

// Convert fixer response ungrouped pairs
// Example input:
//   { base: 'EUR', pairs: ['USD', 'ARS'] }
// Example of fixer result:
//   { success: true, timestamp: 1620000000, base: 'EUR',
//     rates: { USD: 1.123, ARS: 123.45 } }
// Example of result:
//   { pair: { from: 'EUR', to: 'USD' }, originalRate: 1.123 }
//   { pair: { from: 'EUR', to: 'ARS' }, originalRate: 123.45 }
const fixerResponseToPairInfo = (fixerResponse: FixerResponse, tryReverse: boolean) => {
  const {
    base, rates, error,
  } = fixerResponse.data;
  // If error, return the error or inverse pairs if error.code === 105
  if (error?.code === 105 && tryReverse) {
    const reversedPairs = Object.entries(rates || []).map(([to]) => ({
      pair: { from: to as CurrencySymbol, to: base as CurrencySymbol },
      reversed: error?.code === 105 ? true : undefined,
    }));
    return reversedPairs as PairInfo[];
  }
  // Ungroup pairs
  const pairsInfo = Object.entries(rates || {}).map(([to, originalRate]) => {
    const pair = { from: base, to };
    return { pair, originalRate, error } as PairInfo;
  });
  return pairsInfo as PairInfo[];
};

// Process reversed pairs
const processReversedPairs = async (_pairsInfo: PairInfo[]) => {
  const resultPairsInfo = _pairsInfo.filter((pairInfo) => !pairInfo.reversed);
  const reversedPairs = _pairsInfo.filter((pairInfo) => pairInfo.reversed);
  const pairsToProcess = groupFixerPairs(reversedPairs.map((pairInfo) => pairInfo.pair));
  await Promise.all(
    pairsToProcess.map(async (pairToProcess) => {
      const response = await getFixerRate(pairToProcess.base, pairToProcess.symbols);
      const pairsInfo = fixerResponseToPairInfo(response, false);
      const reversedInfo = pairsInfo.map((pairInfo) => ({
        ...pairInfo,
        pair: { from: pairInfo.pair.to, to: pairInfo.pair.from },
        originalRate: pairInfo.originalRate ? 1 / pairInfo.originalRate : undefined,
      }));
      resultPairsInfo.push(...reversedInfo);
    }),
  );
  return resultPairsInfo;
};

// Get rates from Fixer API
export const getFixerRates = async (pairs: Pair[]) => {
  const pairsInfo:PairInfo[] = [];
  const fixerPairs = groupFixerPairs(pairs);
  await Promise.all(
    fixerPairs.map(async (fixerPair) => {
      const { base, symbols } = fixerPair;
      const response = await getFixerRate(base, symbols);
      console.log('response', response.data);
      const pairInfo = fixerResponseToPairInfo(response, true);
      pairsInfo.push(...pairInfo);
    }),
  );
  console.log('pairsInfo', pairsInfo);
  const pairsInfoWithReversed = await processReversedPairs(pairsInfo);
  console.log('pairsInfoWithReversed', pairsInfoWithReversed);
  return pairsInfoWithReversed;
};

import { Request, ResponseToolkit } from '@hapi/hapi';
import Joi from '@hapi/joi';
import { AppController } from '../typings';

class HealthController implements AppController {
  public handler = async (request: Request, response: ResponseToolkit) => {
    const healthcheck = {
      uptime: process.uptime(),
      message: 'OK',
      timestamp: Date.now(),
    };
    return response.response(healthcheck).code(200);
  };

  public plugins = {
    'hapi-swagger': {
      responses: {
        200: {
          description: 'Success',
          schema: Joi.object({
            rates: Joi.object().pattern(Joi.string(), Joi.number()),
          }),
        },
      },
    },
  };

  public options = {
    auth: false,
  };
}

export default HealthController;
